// This node returns exactly ONE item: the newest recording by created_date
// Works with BOTH shapes:
// 1) items = [{json:{page, total_items, data:[{...recording...}, ...]}}, ...]  // multiple page responses
// 2) items = [{json:{recording_id, created_date, ...}}, ...]                  // already split into recordings

function collectRecordings(items) {
  const out = [];
  for (const it of items) {
    const j = it?.json ?? {};
    if (Array.isArray(j.data)) {
      // Page response shape
      for (const r of j.data) out.push(r);
    } else if (j && j.recording_id && j.created_date) {
      // Already-split recording shape
      out.push(j);
    }
  }
  return out;
}

const recordings = collectRecordings(items);

// If nothing, stop
if (!recordings.length) return [];

// Sort newest first by created_date
recordings.sort((a, b) => new Date(b.created_date) - new Date(a.created_date));

// Take the top (latest)
const latest = recordings[0];

// Convenience: surface common transcription URLs if available
const transcription = latest?.transcription ?? {};
const tFiles = Array.isArray(transcription.files) ? transcription.files : [];
const transcriptTxtUrl = tFiles.find(f => f.type === 'txt')?.download_url ?? null;
const transcriptSrtUrl = tFiles.find(f => f.type === 'srt')?.download_url ?? null;

// Return full original object + a helpful _meta block
return [{
  json: {
    ...latest,
    _meta: {
      picked_by: "created_date desc",
      transcript_txt_url: transcriptTxtUrl,
      transcript_srt_url: transcriptSrtUrl
    }
  }
}];

// Access the full JSON from the earlier "Code in JavaScript" node
const prev = $('Code in JavaScript').first().json;

// Return the full JSON unchanged
return [
  {
    json: prev
  }
];

