// n8n JavaScript Code Node
// This code processes input items and generates a status report

// Get all input items
const items = $input.all();

// Count records by status
const statusCounts = {};
let totalRecords = 0;

items.forEach(item => {
  totalRecords++;
  const status = item.json.status || 'NO_STATUS';
  statusCounts[status] = (statusCounts[status] || 0) + 1;
});

// Create status breakdown with percentages
const statusBreakdown = Object.entries(statusCounts).map(([status, count]) => ({
  status,
  count,
  percentage: ((count / totalRecords) * 100).toFixed(2)
}));

// Sort by count (descending)
statusBreakdown.sort((a, b) => b.count - a.count);

// Create formatted WhatsApp message
let message = `üìä *STATUS REPORT*\n`;
message += `${'='.repeat(30)}\n\n`;
message += `üìÖ *Generated:* ${new Date().toLocaleString('en-US', { 
  timeZone: 'Asia/Karachi',
  dateStyle: 'medium',
  timeStyle: 'short'
})}\n`;
message += `üìà *Total Records:* ${totalRecords}\n\n`;
message += `*Status Breakdown:*\n`;
message += `${'-'.repeat(30)}\n\n`;

// Add each status with count and percentage
statusBreakdown.forEach(item => {
  message += `‚ñ™Ô∏è *${item.status}*\n`;
  message += `   Count: ${item.count} (${item.percentage}%)\n\n`;
});

message += `${'-'.repeat(30)}`;

// Create the final report
const report = {
  totalRecords,
  reportGeneratedAt: new Date().toISOString(),
  statusCounts,
  statusBreakdown,
  whatsappMessage: message
};

// Return the report as output
return [
  {
    json: report
  }
];

// Rate limiting check
const rateLimit = $input.all();
const messageCount = rateLimit.length;
const MAX_MESSAGES_PER_5MIN = 10;
const message = $('WhatsApp Trigger1').first().json.messages[0].text.body.toLowerCase();

if (messageCount >= MAX_MESSAGES_PER_5MIN) {
  return {
    isSpam: true,
    reason: 'rate_limit_exceeded',
    messageCount: messageCount,
    action: 'block_temporary',
    message: message
  };
}

// Check for spam keywords
const spamKeywords = [
  'click here', 'free money', 'winner', 'urgent action',
  'limited time', 'act now', 'bitcoin', 'crypto investment'
];

const containsSpam = spamKeywords.some(keyword => message.includes(keyword));

if (containsSpam) {
  return {
    isSpam: true,
    reason: 'spam_keywords_detected',
    action: 'review_required',
    message: message
  };
}

// Check message patterns
const urlPattern = /(https?:\/\/[^\s]+)/g;
const urls = message.match(urlPattern) || [];

if (urls.length > 3) {
  return {
    isSpam: true,
    reason: 'excessive_urls',
    urlCount: urls.length,
    action: 'review_required',
    message: message
  };
}

return {
  isSpam: false,
  reason: 'passed_all_checks',
  action: 'continue',
  message: message,
  messageCount: messageCount
};

